#!/usr/bin/env python3
"""
Companion AI CLI Tool
Interactive command-line interface for the Companion API
"""

import requests
import json
import sys
from pathlib import Path
from datetime import datetime

API_URL = "http://localhost:5000"
CURRENT_CONV_FILE = ".current_conversation"

class CompanionCLI:
    def __init__(self):
        self.api_url = API_URL
        self.current_conversation_id = self.load_current_conversation()
        self.verify_api_running()

    def verify_api_running(self):
        """Check if API is reachable"""
        try:
            response = requests.get(f"{self.api_url}/health", timeout=2)
            if response.status_code == 200:
                data = response.json()
                print(f"✓ Connected to Companion API (v{data.get('version', '?')})")
                print(f"  Model: {data.get('model')}\n")
            else:
                print("✗ API returned non-200 status")
                sys.exit(1)
        except requests.ConnectionError:
            print(f"✗ Cannot reach API at {self.api_url}")
            print("  Make sure the server is running:")
            print("  python companion_api_v2.py")
            sys.exit(1)

    def load_current_conversation(self):
        """Load current conversation ID from file"""
        try:
            with open(CURRENT_CONV_FILE, 'r') as f:
                conv_id = f.read().strip()
                return int(conv_id) if conv_id.isdigit() else None
        except:
            return None

    def save_current_conversation(self, conv_id):
        """Save current conversation ID"""
        with open(CURRENT_CONV_FILE, 'w') as f:
            f.write(str(conv_id))
        self.current_conversation_id = conv_id

    def new_conversation(self, title=""):
        """Create new conversation"""
        try:
            response = requests.post(
                f"{self.api_url}/conversations",
                json={"title": title or f"Chat {datetime.now().strftime('%Y-%m-%d %H:%M')}"}
            )
            if response.status_code == 201:
                data = response.json()
                self.save_current_conversation(data['id'])
                print(f"✓ New conversation created (ID: {data['id']})")
                return data['id']
            else:
                print(f"✗ Error: {response.json()['error']}")
                return None
        except Exception as e:
            print(f"✗ Error: {e}")
            return None

    def list_conversations(self):
        """List recent conversations"""
        try:
            response = requests.get(f"{self.api_url}/conversations")
            if response.status_code == 200:
                convs = response.json()['conversations']
                if not convs:
                    print("No conversations yet.")
                    return

                print("\n📋 Recent Conversations:")
                print("-" * 60)
                for conv in convs:
                    marker = "→" if conv['id'] == self.current_conversation_id else " "
                    print(f"{marker} [{conv['id']:3}] {conv['title']:<35} {conv['updated_at'][:10]}")
                print("-" * 60)
            else:
                print(f"✗ Error: {response.json()['error']}")
        except Exception as e:
            print(f"✗ Error: {e}")

    def switch_conversation(self, conv_id):
        """Switch to a different conversation"""
        try:
            response = requests.get(f"{self.api_url}/conversations/{conv_id}")
            if response.status_code == 200:
                self.save_current_conversation(conv_id)
                msgs = response.json()['messages']
                print(f"✓ Switched to conversation {conv_id} ({len(msgs)} messages)")
            else:
                print(f"✗ Conversation not found")
        except Exception as e:
            print(f"✗ Error: {e}")

    def view_conversation(self, conv_id=None):
        """View conversation history"""
        conv_id = conv_id or self.current_conversation_id
        if not conv_id:
            print("✗ No conversation selected. Use 'new' or 'switch <id>'")
            return

        try:
            response = requests.get(f"{self.api_url}/conversations/{conv_id}")
            if response.status_code == 200:
                msgs = response.json()['messages']
                print(f"\n📝 Conversation {conv_id}:")
                print("-" * 70)
                for msg in msgs:
                    role = "👤 You" if msg['role'] == 'user' else "🤖 AI"
                    timestamp = msg['timestamp'][:16]
                    print(f"\n{role} [{timestamp}]")
                    print(f"  {msg['content']}")
                print("-" * 70)
            else:
                print(f"✗ Error: {response.json()['error']}")
        except Exception as e:
            print(f"✗ Error: {e}")

    def chat(self, message):
        """Send a message and get response"""
        conv_id = self.current_conversation_id
        if not conv_id:
            print("Creating new conversation...")
            conv_id = self.new_conversation()
            if not conv_id:
                return

        try:
            print("⏳ Waiting for response...")
            response = requests.post(
                f"{self.api_url}/chat",
                json={
                    "message": message,
                    "conversation_id": conv_id
                },
                timeout=120
            )

            if response.status_code == 200:
                data = response.json()
                print(f"\n🤖 AI:\n{data['response']}\n")
            else:
                print(f"✗ Error: {response.json()['error']}")
        except requests.Timeout:
            print("✗ Request timeout (LLM taking too long)")
        except Exception as e:
            print(f"✗ Error: {e}")

    def show_help(self):
        """Show help message"""
        print("""
╔════════════════════════════════════════════════════════════════╗
║           Companion AI CLI - Commands                          ║
╚════════════════════════════════════════════════════════════════╝

📝 Conversation Management:
  new [title]           Create new conversation
  list / ls             List recent conversations
  switch <id>           Switch to conversation ID
  view [id]             View conversation history (current or specified)

💬 Chat:
  (just type)           Send message to current conversation

🔧 System:
  config                Show current configuration
  help                  Show this help
  exit / quit           Exit CLI

📌 Examples:
  > new "Project Notes"
  > What is machine learning?
  > switch 3
  > view
  > list
        """)

    def run(self):
        """Main CLI loop"""
        print("\n🤖 Companion AI CLI")
        print("Type 'help' for commands, or just start typing to chat!")
        print()

        while True:
            try:
                # Show prompt
                conv_label = f"Conv {self.current_conversation_id}" if self.current_conversation_id else "No Conv"
                prompt = f"[{conv_label}] > "

                user_input = input(prompt).strip()

                if not user_input:
                    continue

                # Parse commands
                parts = user_input.split(maxsplit=1)
                command = parts[0].lower()
                args = parts[1] if len(parts) > 1 else ""

                if command in ['exit', 'quit']:
                    print("Goodbye!")
                    break

                elif command == 'help':
                    self.show_help()

                elif command == 'new':
                    self.new_conversation(args)

                elif command in ['list', 'ls']:
                    self.list_conversations()

                elif command == 'switch':
                    if args.isdigit():
                        self.switch_conversation(int(args))
                    else:
                        print("✗ Usage: switch <conversation_id>")

                elif command == 'view':
                    conv_id = int(args) if args.isdigit() else None
                    self.view_conversation(conv_id)

                elif command == 'config':
                    try:
                        resp = requests.get(f"{self.api_url}/config")
                        if resp.status_code == 200:
                            print("\n⚙️ Current Config:")
                            print(json.dumps(resp.json(), indent=2))
                    except Exception as e:
                        print(f"✗ Error: {e}")

                else:
                    # Treat as chat message
                    self.chat(user_input)

            except KeyboardInterrupt:
                print("\n\nGoodbye!")
                break
            except Exception as e:
                print(f"✗ Error: {e}")

if __name__ == '__main__':
    cli = CompanionCLI()
    cli.run()

